import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchP } from '../Actions';


class PharmacyList extends Component {

    componentDidMount(){
        this.props.fetchPharmacys();
    }

    renderList() {
        const emptyMessage = (
            <div>
                <h4>Empty</h4>
            </div>
        );

        if(this.props.pharmacys.length === 0) {
            return(
                emptyMessage
            );
        }
        else
            return this.props.pharmacys.map(item => {
                return(
                    <div>
                    <p>{item.eczane_adi}</p>
                    <p>{item.eczane_ilce}</p>
                    </div>
                );
            });
        
    }

    render(){
        return(
            <div>
                <h2 style={{marginTop:10}}>DATAS</h2>
                {this.renderList()}
            </div>
        );
    }

 };

 function mapStateToProps(state){
     return { pharmacys: state.pharmacys };
 }

 export default connect(mapStateToProps, {fetchPharmacys})(PharmacyList);
