import axios from 'axios';
import { FETCH_DATA } from './types';

const url = 'http://api.kodlama.net/eczane/il/34';
const urll = 'http://localhost:3001';


export const fetchData = () => async dispatch => {
  
  const { data : { eczane_adi , id} } = await axios.get('http://api.kodlama.net/eczane/all');
  const res = { eczane_adi , id }
  dispatch({ type: FETCH_DATA, payload: res.data });
}


export const fetchData2 = async (country) => {
    let changeableUrl = url ;
  
  
    if(country){
      changeableUrl = `${url}/il/${country}`
    }
    else{
      changeableUrl = `${url}/all`
    }
  
    try{
      const { data } = await axios.get(changeableUrl);
  
      console.log(changeableUrl);
      return data;

      
    }
    catch (error){
      console.log(error)
    }
  }

   
  export const fetchCountries = async () => {
    try {
      const { data } = await axios.get(`${urll}/countries`);
  
      return data;
  
    }
    catch(error) {
      console.log(error)
    }
  }
