import { FETCH_DATA, FILTER_DATA_COUNTRY } from '../Actions/types';


const initialState= { items: [], filteredItems: [], country: ''};
export default (state = initialState, action) => {
    switch (action.type) {
        case FETCH_DATA: 
        return {
            ...state,
            items: action.pa
        }
        case FILTER_DATA_COUNTRY: 
        return { ...state, filteredItems: action.payload.items, country: action.payload.country}
        default: 
        return state;
    }
}
