import { FETCH_DATA, FILTER_DATA_COUNTRY } from './types';

import axios from 'axios';

const url = 'http://api.kodlama.net/eczane/all';




export const fetchData = () => async dispatch => {
    const res = await axios.get(url)

    dispatch({ type: FETCH_DATA, payload: res.data });
}

export const filterData = (datas, country) => async dispatch => {

    return dispatch({
        type: FILTER_DATA_COUNTRY,
        payload: {
            
            items:country === ''? datas: datas.filter(a => a.availableSizes.indexOf(country.toUpperCase())>=0)
        }
    })
        

   
}
