import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchData } from '../Actions/index';
import './PharmacyList.css';
import { Card, CardContent, Typography, Grid } from '@material-ui/core';


class PharmacyList extends Component {

  
  handleValue = (e) => this.setState({ search: e.target.value })

  componentDidMount() {
    const country = this.props.country;
    this.props.fetchData(country);
  } 
  componentDidUpdate(prevProps){
      if(this.props.country !== prevProps.country) {
        this.props.fetchData(this.props.country)
      }
  }
  state = {
    search: '',
  }
 
    render(){
      
      const { search } = this.state;
      console.log(search);

      const filteredData = this.props.data.filter( item => {
       return item.eczane_ilce.toLowerCase().includes( search.toLowerCase() )
      } )
      
      let dataItems;

      if(this.props.data.length > 0) {
        dataItems = filteredData.map(item => (
          
          <div key={item.id} className="product" >
            <div className="product_info"><p>Eczane Adı:{item.eczane_adi}</p></div>
            <div className="product_info"><p>Eczane İlçesi:{item.eczane_ilce}</p></div>
            <div className="product_info"><p>Eczane Adresi:{item.eczane_adres}</p></div>
            <div className="product_info"><p>Eczane Telefon:{item.eczane_telefon}</p></div>
          </div>
        ));
      }else{
        dataItems = <h4>Empty</h4>
      }
 
      return(
        <div>
          <h4>İlçe İle Sırala</h4>
          <div className="header_search">
          <input  type="text" placeholder="Search" onChange={this.handleValue} />
          </div>
          <div className="container">
          {dataItems}
          </div>
        </div>
      );
    }
}


const mapStateToProps = state => {
  return { data : state.data };
}

export default connect(mapStateToProps, { fetchData })(PharmacyList);
