import { FETCH_DATA, FILTER_DATA_COUNTRY } from './types';

import axios from 'axios';

const url = 'http://api.kodlama.net/eczane/all';




export const fetchData = () => async dispatch => {
    const res = await axios.get(url)

    dispatch({ type: FETCH_DATA, payload: res.data });
}

export const filterData = (country) => async dispatch => {

    
    const res = await axios.get(url)

    dispatch({ type: FETCH_DATA, payload: res.data });
}
