import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchData } from '../Actions';


class PharmacyList extends Component {

    componentDidMount(){
        this.props.fetchData();
    }

    renderList() {
        const emptyMessage = (
            <div>
                <h4>Empty</h4>
            </div>
        );

        if(this.props.data.length === 0) {
            return(
                emptyMessage
            );
        }
        else
            return this.props.data.data.map(item => {
                return(
                    <div>
                        <ul>
                            <li>{item.eczane_adi}</li>
                            <li>{item.eczane_ilce} İlçesi</li>
                            <li>{item.eczane_adres}</li>
                            <li>{item.eczane_telefon}</li>
                            <li>{item.eczane_il}</li>
                        </ul>
                    </div>
                );
            });
        
    }

    render(){
        const { items } = this.props.data;
        let dataİtems;
        if(items === null ){
            <div>Boş</div>
        }else {
            if(items.length > 0){
                dataİtems = items.map(item => (
                    <ul>
                        <li>{item.eczane_adi}</li>
                        <li>{item.eczane_adres}</li>
                    </ul>
                ));
                
            }
        }
        return(
            <div>
                <h2 style={{marginTop:10}}>DATAS</h2>
                {this.renderList()}
            </div>
        );
    }

 };

 function mapStateToProps(state){
     return { data: state.data };
 }

 export default connect(mapStateToProps, {fetchData})(PharmacyList);
